server <- function(input, output, session) {
  # Percentage sum text
  output$pct_sum_text <- renderText({
    pct_sum <- sum(
      input$pct_Splus,
      input$pct_S,
      input$pct_A,
      input$pct_B,
      input$pct_C,
      input$pct_D
    )
    msg <- sprintf("Total percentage: %.1f%%", pct_sum)
    if (abs(pct_sum - 100) < 0.1) {
      paste(msg, "✓")
    } else {
      paste(msg, "✗ (should be 100%)")
    }
  })
  
  # Reactive randomizer
  picked_tracks <- eventReactive(input$randomize, {
    # Get percentages from inputs
    pcts <- c(
      "S+" = input$pct_Splus,
      "S" = input$pct_S,
      "A" = input$pct_A,
      "B" = input$pct_B,
      "C" = input$pct_C,
      "D" = input$pct_D
    )
    
    # Calculate how many tracks to pick from each rating
    total_pct <- sum(pcts)
    n_per_rating <- round(input$n_tracks * pcts / total_pct)
    
    # Adjust to ensure we get exactly n_tracks
    while(sum(n_per_rating) > input$n_tracks) {
      i <- which.max(n_per_rating[n_per_rating > 0])
      n_per_rating[i] <- n_per_rating[i] - 1
    }
    while(sum(n_per_rating) < input$n_tracks) {
      i <- which.max(pcts * (n_per_rating < sapply(list(nrow(tracks_Splus), 
                                                        nrow(tracks_S), 
                                                        nrow(tracks_A), 
                                                        nrow(tracks_B), 
                                                        nrow(tracks_C), 
                                                        nrow(tracks_D)))))
      n_per_rating[i] <- n_per_rating[i] + 1
    }
    
    # Sample from each rating
    result <- tibble(Track = character(), Type = character(), Rating = character())
    
    # S+ tracks
    if (n_per_rating["S+"] > 0) {
      if (n_per_rating["S+"] <= nrow(tracks_Splus)) {
        sampled <- sample_n(tracks_Splus, n_per_rating["S+"])
        result <- bind_rows(result, mutate(sampled, Rating = "S+"))
      } else {
        showNotification("Not enough S+ tracks available", type = "warning")
        result <- bind_rows(result, mutate(tracks_Splus, Rating = "S+"))
      }
    }
    
    # S tracks
    if (n_per_rating["S"] > 0) {
      if (n_per_rating["S"] <= nrow(tracks_S)) {
        sampled <- sample_n(tracks_S, n_per_rating["S"])
        result <- bind_rows(result, mutate(sampled, Rating = "S"))
      } else {
        showNotification("Not enough S tracks available", type = "warning")
        result <- bind_rows(result, mutate(tracks_S, Rating = "S"))
      }
    }
    
    # A tracks
    if (n_per_rating["A"] > 0) {
      if (n_per_rating["A"] <= nrow(tracks_A)) {
        sampled <- sample_n(tracks_A, n_per_rating["A"])
        result <- bind_rows(result, mutate(sampled, Rating = "A"))
      } else {
        showNotification("Not enough A tracks available", type = "warning")
        result <- bind_rows(result, mutate(tracks_A, Rating = "A"))
      }
    }
    
    # B tracks
    if (n_per_rating["B"] > 0) {
      if (n_per_rating["B"] <= nrow(tracks_B)) {
        sampled <- sample_n(tracks_B, n_per_rating["B"])
        result <- bind_rows(result, mutate(sampled, Rating = "B"))
      } else {
        showNotification("Not enough B tracks available", type = "warning")
        result <- bind_rows(result, mutate(tracks_B, Rating = "B"))
      }
    }
    
    # C tracks
    if (n_per_rating["C"] > 0) {
      if (n_per_rating["C"] <= nrow(tracks_C)) {
        sampled <- sample_n(tracks_C, n_per_rating["C"])
        result <- bind_rows(result, mutate(sampled, Rating = "C"))
      } else {
        showNotification("Not enough C tracks available", type = "warning")
        result <- bind_rows(result, mutate(tracks_C, Rating = "C"))
      }
    }
    
    # D tracks
    if (n_per_rating["D"] > 0) {
      if (n_per_rating["D"] <= nrow(tracks_D)) {
        sampled <- sample_n(tracks_D, n_per_rating["D"])
        result <- bind_rows(result, mutate(sampled, Rating = "D"))
      } else {
        showNotification("Not enough D tracks available", type = "warning")
        result <- bind_rows(result, mutate(tracks_D, Rating = "D"))
      }
    }
    
    # Shuffle the results
    sample_n(result, nrow(result))
    })
      
      output$picked_table <- renderTable({
        req(picked_tracks())
        picked_tracks()
      })
      
      # Show all tracks conditionally
      output$all_tracks_ui <- renderUI({
        if (input$show_all) {
          tagList(
            hr(),
            h3("All Tracks"),
            tabsetPanel(
              tabPanel("S+", tableOutput("tracks_Splus_table")),
              tabPanel("S", tableOutput("tracks_S_table")),
              tabPanel("A", tableOutput("tracks_A_table")),
              tabPanel("B", tableOutput("tracks_B_table")),
              tabPanel("C", tableOutput("tracks_C_table")),
              tabPanel("D", tableOutput("tracks_D_table"))
            )
          )
        }
      })
      
      # Render tables for each rating
      output$tracks_Splus_table <- renderTable({
        req(input$show_all)
